sp_widget.client_script:
function($rootScope, $scope) {
	// widget controller
	var c = this;

	c.selectItem = function(idx) {
		// メモID
		var sysId = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + sysId);

		$rootScope.noteID = sysId;
		$rootScope.$emit('selectNote', sysId);
	}
}

sp_widget.script:
(function() {
	// メモを入力する配列
	data.notes = [];

	var grNote = new GlideRecord('x_snc_createnotes_note');
	// 自身のメモ
	grNote.addQuery('user', gs.getUser().getID());
	// 作成日時降順
	grNote.orderByDesc('sys_created_on');
	// 取得
	grNote.query();

	while (grNote.next()) {
		var rowNote = {};
		// 表示値取得
		$sp.getRecordDisplayValues(rowNote, grNote, 'number,title,sys_id');
		// 20文字まで
		rowNote.note = grNote.getValue('note').slice(0,20);
		// 配列に追加
		data.notes.push(rowNote);
	}
})();
